import React from "react";
import {Card, Typography, Divider, Stack, styled} from "@mui/material";
import steak from "/home/pdenert/projects/webdev/recipeapp/client/src/img/steak.jpg";


const RecipeCard = ({name, servings, time, rating}) => {

    const RecipeCard = styled(Card)(() => ({                                //recipe card styling
        backgroundColor: "#eeeeee",
        color: "black",
        margin: "5px",
        width: "100%",
        height: "300px",
        outline: "black",
        transition: "transform 0.15s ease-in-out",
        "&:hover": { transform: "scale(1.03, 1.03)" },
      }));

    return(                                                                         //recipe card object
        <RecipeCard variant="outlined">
            <h1>{name}</h1>
            <Divider sx={{ borderBottomWidth: 2, backgroundColor: "black"}}/>
            <Stack direction='row' spacing={1}>
                <img src={steak} alt={name} height="220px" width="50%"></img>
                <Stack spacing={7} sx={{alignItems: "start"}}>
                    <Typography variant="h6">Servings: {servings}</Typography>
                    <Typography variant="h6">Time: {time} minutes</Typography>
                    <Typography variant="h6">Rating: {rating}/10</Typography>
            </Stack>
            </Stack>
        </RecipeCard>);
}

export default RecipeCard;
