import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import {Stack } from "@mui/material";


const RecipeDetails = () => {
    const [form, setForm] = useState({                              //store recipe from mongodb
        name: "",
        servings: "",
        time: "",
        rating: "",
        base64Img: "",
      });

     const { _id } = useParams();                                   //get id from route
     const navigate = useNavigate();

    useEffect(() => {
    async function fetchData() {                                    //get recipe from mongodb
        const response = await fetch(`http://localhost:5050/record/${_id.toString()}`);
        if (!response.ok) {
           const message = `An error has occurred: ${response.statusText}`;
           window.alert(message);
           return;
        }

        const recipe = await response.json();
        console.log("hello");
        if (!recipe) {
            window.alert(`Recipe with id ${_id} not found`);
           navigate("/recipes");
           return;
         }
         setForm(recipe);

       }

       fetchData();
       return;
    }, [_id, navigate]);
    return(
        <Stack sx={{ml: -1, mr: -1}} paddingTop='75px' textAlign='center' color={"text.primary"} bgcolor={"background.default"}>
            <h1>{form.name}</h1>
            <h1>{form.servings}</h1>
            <h1>{form.time}</h1>
            <h1>{form.rating}</h1>
            <h1>{form.base64Img}</h1>
        </Stack>
        );
}

export default RecipeDetails;
