import React from "react";
import {Stack, Grid} from "@mui/material";
import RecipeCard from "./recipecard";

class Recipe {
    constructor(name,servings,time,rating) {
        this.name = name;
        this.servings = servings;
        this.time = time;
        this.rating = rating;
    }
}

const steak = new Recipe("Steak","1","200","10")
const pennevodka = new Recipe("Penne A La Vodka","1","60","9")
const crunchwrap = new Recipe("Crunchwrap","1","30","9")
const curry = new Recipe("Curry","1","75","8")

const recipeArray = [steak,pennevodka,crunchwrap,curry]

const Recipes = () => {
    return(
        <Stack sx={{ml: -1, mr: -1}} paddingTop='75px' textAlign='center' color={"text.primary"} bgcolor={"background.default"}>
            <h1>Recipes</h1>
            <Grid container xs={12}>
            {recipeArray.map((recipe) => (
                <Grid item xs><RecipeCard name={recipe.name} servings={recipe.servings} time={recipe.time} rating={recipe.rating}></RecipeCard></Grid>
            ))}
        </Grid>
        </Stack>
        );
}

export default Recipes;
