import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import {Grid, Stack } from "@mui/material";


const RecipeDetails = () => {
    const [form, setForm] = useState({                              //store recipe from mongodb
        name: "",
        servings: "",
        time: "",
        rating: "",
        base64Img: "",
      });

     const { _id } = useParams();                                   //get id from route
     const navigate = useNavigate();
    useEffect(() => {
    async function fetchData() {                                    //get recipe from mongodb
        const response = await fetch(`http://localhost:5050/record/${_id.toString()}`);
        if (!response.ok) {
           const message = `An error has occurred: ${response.statusText}`;
           window.alert(message);
           return;
        }

        const recipe = await response.json();
        console.log("hello");
        if (!recipe) {
            window.alert(`Recipe with id ${_id} not found`);
           navigate("/recipes");
           return;
         }
         setForm(recipe);

       }

       fetchData();
       return;
    }, [_id, navigate]);
    return(
        <Stack sx={{ml: -1, mr: -1,}} paddingTop='75px' textAlign='center' color={"text.primary"} bgcolor={"secondary.main"} spacing={0} >
            <Stack sx={{mt: 3, ml: 5, mr: 5, border: 2, borderRadius: '16px'}} bgcolor={"#eeeeee"} >
            <h1>{form.name}</h1>
                <Grid container spacing={0}>
                    <Grid item xs={12} lg={4}><h1>Servings: {form.servings}</h1></Grid>
                    <Grid item xs={12} lg={4}><h1>Time: {form.time}</h1></Grid>
                    <Grid item xs={12} lg={4}><h1>Rating: {form.rating}/10</h1></Grid>
                </Grid>
                <div sx={{textAlign: "center"}}><img src={form.base64Img} alt={form.name} height="400px" width="400px"></img></div>
            </Stack>
        </Stack>
        );
}

export default RecipeDetails;
