import React from "react";
import {Stack } from "@mui/material";

const About = () => {
    return(
        <Stack sx={{ml: -1, mr: -1, textAlign: "justify"}} paddingTop='75px' textAlign='center' color={"text.primary"} bgcolor={"background.default"}>
            <h1>About</h1>
            <p>This Website is a place to upload recipes and view other people's recipes. This website is developed by Patrick Denert, a recent Computer Engineering Graduate looking for a full time position</p>
            <p>Future Updates planned:</p>
            <li>Accounts</li>
            <li>User reviews/comments</li>
            <li>efficiency</li>
        </Stack>
        );
}

export default About;
