import React from "react";
import {Stack, Grid} from "@mui/material";
import RecipeCard from "./recipecard";
import steakImg from "/home/pdenert/projects/webdev/recipeapp/client/src/img/steak.jpg";
import penneImg from "/home/pdenert/projects/webdev/recipeapp/client/src/img/penne.png";
import crunchwrapImg from "/home/pdenert/projects/webdev/recipeapp/client/src/img/crunchwrap.png";
import curryImg from "/home/pdenert/projects/webdev/recipeapp/client/src/img/curry.png";


class Recipe {
    constructor(name,servings,time,rating,img) {
        this.name = name;
        this.servings = servings;
        this.time = time;
        this.rating = rating;
        this.img = img;
    }
}

const steak = new Recipe("Steak","1","200","10", steakImg)
const pennevodka = new Recipe("Penne A La Vodka","1","60","9",penneImg)
const crunchwrap = new Recipe("Crunchwrap","1","30","9",crunchwrapImg)
const curry = new Recipe("Curry","1","75","8",curryImg)

const recipeArray = [steak,pennevodka,crunchwrap,curry]

const Recipes = () => {
    return(
        <Stack sx={{ml: -1, mr: -1}} paddingTop='75px' textAlign='center' color={"text.primary"} bgcolor={"background.default"}>
            <h1>Recipes</h1>
            <Grid container spacing={2}>
                {recipeArray.map((recipe) => (
                    <Grid item xs={12} lg={4}>
                        <RecipeCard name={recipe.name} servings={recipe.servings} time={recipe.time} rating={recipe.rating} img={recipe.img}>
                        </RecipeCard>
                    </Grid>
                ))}
            </Grid>
        </Stack>
        );
}

export default Recipes;
