import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import {Stack } from "@mui/material";


const RecipeDetails = () => {
    const [form, setForm] = useState({
        name: "",
        servings: "",
        time: "",
        rating: "",
      });
     const { _id } = useParams();
     const navigate = useNavigate();

    async function fetchData() {
        console.log(_id);
        const response = await fetch(`http://localhost:5050/record/${_id.toString()}`);

        if (!response.ok) {
           const message = `An error has occurred: ${response.statusText}`;
           window.alert(message);
           return;
        }

        const recipe = await response.json();
        console.log("hello");
        if (!recipe) {
            window.alert(`Recipe with id ${_id} not found`);
           navigate("/list");
           return;
         }
         setForm(recipe);

       }

       fetchData();


     // These methods will update the state properties.
     function updateForm(value) {
       return setForm((prev) => {
         return { ...prev, ...value };
       });
     }
    return(
        <Stack sx={{ml: -1, mr: -1}} paddingTop='75px' textAlign='center' color={"text.primary"} bgcolor={"background.default"}>
            <h1>{form.name}</h1>
        </Stack>
        );
}

export default RecipeDetails;
