import React from "react";
import {Card, CardHeader, Stack, styled} from "@mui/material";



const RecipeCard = ({name, servings, time}) => {

    const RecipeCard = styled(Card)(({ main }) => ({                                //searchbar
        backgroundColor: "#D2B48C",
        color: "black",
        margin: "5px",
        width: "30%",
        height: "300px",
      }));

    return(
        <RecipeCard variant="outlined">
            <h1>{name}</h1>
            <h2>Servings: {servings} Time: {time}</h2>
            <Stack direction='row'>
                <h2>Servings: {servings} Time: {time}</h2>
            </Stack>
        </RecipeCard>);
}

export default RecipeCard;
