import React from "react";
import {Stack} from "@mui/material";
import RecipeCard from "./recipecard";

class Recipe {
    constructor(name,servings,time,rating) {
        this.name = name;
        this.servings = servings;
        this.time = time;
        this.rating = rating;
    }
}

const steak = new Recipe("Steak","1","200","10")

const recipeArray = [steak]

const Recipes = () => {
    return(
        <Stack sx={{ml: -1, mr: -1}} paddingTop='75px' textAlign='center' color={"text.primary"} bgcolor={"background.default"}>
            <h1>Recipes</h1>
            {settings.map((setting) => (
            <MenuItem key={setting} onClick={handleCloseUserMenu}>
              <Typography textAlign="center">{setting}</Typography>
            </MenuItem>
          ))}
            <RecipeCard name={steak.name} servings={steak.servings} time={steak.time} rating={steak.rating}></RecipeCard>

        </Stack>
        );
}

export default Recipes;
