import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import {Divider, Grid, Stack } from "@mui/material";
import Rating from '@mui/material/Rating';

const RecipeDetails = () => {
    const [form, setForm] = useState({                              //store recipe from mongodb
        name: "",
        servings: "",
        time: "",
        rating: "",
        base64Img: "",
        ingredients: [{}],
      });

     const { _id } = useParams();                                   //get id from route
     const navigate = useNavigate();
    useEffect(() => {
    async function fetchData() {                                    //get recipe from mongodb
        const response = await fetch(`http://localhost:5050/record/${_id.toString()}`);
        if (!response.ok) {
           const message = `An error has occurred: ${response.statusText}`;
           window.alert(message);
           return;
        }

        const recipe = await response.json();
        console.log("hello");
        if (!recipe) {
            window.alert(`Recipe with id ${_id} not found`);
           navigate("/recipes");
           return;
         }
         setForm(recipe);
         console.log(recipe);
       }
       fetchData();
       return;
    }, [_id, navigate]);
    return(
        <Stack sx={{ml: -1, mr: -1,}} paddingTop='75px' textAlign='center' color={"text.primary"} bgcolor={"background.default"} >
            <Stack sx={{mt: 3, ml: 5, mr: 5, border: 2, borderRadius: '16px'}} bgcolor={"#eeeeee"} >
            <h1>{form.name}</h1>
            <Divider sx={{ borderBottomWidth: 1, backgroundColor: "black", width: "33%", ml: "33%"}}/>
                <Grid container>
                    <Grid item xs={12} lg={4}><h2>Servings: {form.servings}</h2></Grid>
                    <Grid item xs={12} lg={4}><h2>Time: {form.time} minutes</h2></Grid>
                    <Grid item xs={12} lg={4}><h2>Rating:</h2><Rating value={form.rating} readOnly></Rating></Grid>
                </Grid>
                <div sx={{textAlign: "center"}}><img src={form.base64Img} alt={form.name} height="400px" width="400px"></img></div>
                <h2>Ingredients: </h2>
                {form.ingredients.map(ingredient => (
                    <h3>{ingredient.ingredient} {ingredient.amount} {ingredient.measurement}</h3>
                ))}
                <pre sx={{textAlign: "left"}}>{form.instructions}</pre>
            </Stack>
        </Stack>
        );
}

export default RecipeDetails;
