import React, { useState } from "react";
import { useNavigate } from "react-router";



export default function Create() {
 const [form, setForm] = useState({
   name: "",
   servings: "",
   time: "",
   rating: "",
   base64Img: "",
   ingredients: [{ ingredient: "", amount: "", measurement: ""}],
 });
 const navigate = useNavigate();

 const [ingredientFields, setIngredientFields] = useState([
  { ingredient: "", amount: "", measurement: ""}
 ])



 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }

 function updateIngredient(value) {
  return setIngredientFields((prev) => {
    console.log(ingredientFields);
    return { ...prev, ...value };
  });
}

 const handleFileUpload = async (e) => {
  const file = e.target.files[0];
  const base64 = await convertToBase64(file);
  console.log("", base64);
  updateForm( { base64Img: base64 });

 }

 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();

   // When a post request is sent to the create url, we'll add a new record to the database.
   const newRecipe = { ...form };

   await fetch("http://localhost:5050/record", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newRecipe),
   })
   .catch(error => {
     window.alert(error);
     return;
   });

   setForm({ name: "", servings: "", time: "", rating: "", base64img: "" });
   navigate("/");
 }

 // This following section will display the form that takes the input from the user.
 return (
   <div>
     <h3>Create New Recipe</h3>
     <form onSubmit={onSubmit}>
       <div className="form-group">
         <label htmlFor="name">Name</label>
         <input
           type="text"
           className="form-control"
           id="name"
           value={form.name}
           onChange={(e) => updateForm({ name: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="servings">Servings</label>
         <input
           type="text"
           className="form-control"
           id="servings"
           value={form.servings}
           onChange={(e) => updateForm({ servings: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="time">Time</label>
         <input
           type="text"
           className="form-control"
           id="time"
           value={form.time}
           onChange={(e) => updateForm({ time: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="rating">Rating</label>
         <input
           type="text"
           className="form-control"
           id="rating"
           value={form.rating}
           onChange={(e) => updateForm({ rating: e.target.value })}
         />
       </div>
       <div>
        <form>
       { ingredientFields.map((ingredientFields) => {
          return(
          <div className="form-group">
            <label htmlFor="time">Ingredient: </label>
            <input
            type="text"
            className="form-control"
            id="ingredient"
            value={ingredientFields.ingredient}
            onChange={(e) => updateIngredient({ ingredient: e.target.value })}
            />
            <label htmlFor="time">Amount: </label>
            <input
            type="text"
            className="form-control"
            id="amount"
            value={ingredientFields.amount}
            onChange={(e) => updateIngredient({ amount: e.target.value })}
            />
            <label htmlFor="time">Measurement: </label>
            <input
            type="text"
            className="form-control"
            id="measurement"
            value={ingredientFields.measurement}
            onChange={(e) => updateIngredient({ measurement: e.target.value })}
            />
          </div>
          )
          })}
          </form>
       </div>
       <div className="form-group">
         <label htmlFor="file-upload">Image</label>
         <input
           type="file"
           className="form-control"
           id="file-upload"
           accept=".jpeg, .png, .jpg"
           onChange={(e) => handleFileUpload(e)}
         />
       </div>
       <div className="form-group">
         <input
           type="submit"
           value="Create Recipe"
           className="btn btn-primary"
         />
       </div>


     </form>
   </div>
 );
}

function convertToBase64(file){
  return new Promise((resolve, reject) => {
    const fileReader = new FileReader();
    fileReader.readAsDataURL(file);
    fileReader.onload = () => {
      resolve(fileReader.result)
    };

    fileReader.onerror = (error) => {
      reject(error)
    }
  })
}
