import React from "react";
import {Card, Typography, Divider, Stack, styled} from "@mui/material";
import steak from "/home/pdenert/projects/webdev/recipeapp/client/src/img/steak.jpg";


const RecipeCard = ({name, servings, time, rating}) => {

    const RecipeCard = styled(Card)(() => ({                                //searchbar
        backgroundColor: "#1b5e20",
        color: "white",
        margin: "5px",
        width: "30%",
        height: "300px",
      }));

    return(
        <RecipeCard variant="outlined">
            <h1>{name}</h1>
            <Divider sx={{ borderBottomWidth: 2, backgroundColor: "black"}}/>
            <Stack direction='row' spacing={1}>
                <img src={steak} alt={name} height="220px" width="50%"></img>
                <Stack spacing={0}>
                <Typography sx={{align: "left"}}>
                    <Typography sx={{align: "left"}}></Typography>
                    <h2>Time: {time}</h2>
                    <h2>Rating: {rating}</h2>
                </Typography>
            </Stack>
            </Stack>
        </RecipeCard>);
}

export default RecipeCard;
